        const created = new Date(pr.createdAt);
        const merged = new Date(pr.mergedAt);
        return (merged - created) / (1000 * 60 * 60); // hours
      });

      const avgLeadTime = leadTimes.reduce((sum, time) => sum + time, 0) / leadTimes.length;
